import React, { Component } from 'react';
import {geolocated} from 'react-geolocated';

class MyComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      myCoords: {},
      address: null,
      items: []
    };
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    // Store myCoords in state so we can compare when props change
    if (nextProps.coords !== prevState.coords) {
      return {
        myCoords: nextProps.coords,
      };
    }

    // No state update necessary
    return null;
  }

  componentDidMount() {


  }

  componentDidUpdate(prevProps, prevState) {
      if (this.props.coords) {
      this._loadWhat3WordData();
    }
  }

  _loadWhat3WordData() {
    fetch("https://api.what3words.com/v2/reverse?coords="+this.state.myCoords.lat+"%2C"+this.state.myCoords.long+"&key=DECNMYWK")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            address: result.words,
            items: result
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }


  getCoordsDom() {
    return !this.props.isGeolocationAvailable
      ? <div>Your browser does not support Geolocation</div>
      : !this.props.isGeolocationEnabled
        ? <div>Geolocation is not enabled</div>
        : this.props.coords
          ? <table>
            <tbody>
              <tr><td>latitude</td><td>{this.props.coords.latitude}</td></tr>
              <tr><td>longitude</td><td>{this.props.coords.longitude}</td></tr>
            </tbody>
          </table>
          : <div>Getting the location data&hellip; </div>;
  }

  render() {
    const { error, isLoaded, myCoords, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div>
        <p>
          {items.words}
        </p>
      <table>
      <tbody>
        <tr><td>latitude</td><td>{myCoords.latitude}</td></tr>
              <tr><td>longitude</td><td>{myCoords.longitude}</td></tr>
            </tbody>
            </table>
        </div>
      );
    }
  }
}

export default geolocated({
  positionOptions: {
    enableHighAccuracy: false,
  },
  userDecisionTimeout: 5000,
}) (MyComponent);
