import React, { Component } from 'react';
import 'react-geolocated';

class MyComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      myCoords: null,
      address: null,
      items: []
    };
  }

  componentDidMount() {
    getCoords()
    fetch("https://api.what3words.com/v2/reverse?coords=51.521251%2C-0.203586&key=DECNMYWK")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            address: result.words,
            items: result
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  getCoords() {
      console.log(this.props);
      if (this.props.isGeolocationAvailable) {
          this.setState((props) => ({
              coords: props.coords 
          }));

          console.log(this.props.coords.latitude);

      }

  }

  render() {
    const { error, isLoaded, myCoords, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div>
        <p>
          {items.words}
        </p>
        <p>
          {myCoords}
        </p>
        </div>
      );
    }
  }
}

export default MyComponent;
