import React, { Component } from 'react';
import {geolocated} from 'react-geolocated';

class MyComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      myCoords: {lat:null,long:null},
      address: null,
      items: []
    };
  }

  componentDidMount() {
    fetch("https://api.what3words.com/v2/reverse?coords=59.521251%2C-0.203586&key=DECNMYWK")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            address: result.words,
            items: result
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  getCoords() {
      this.setState(prevState => ({
        myCoords: {
        ...prevState.myCoords,
        lat: this.props.coords.latitude,
        long: this.props.coords.longitude
        }
      }))
  }

  getCoordsDom() {
    return !this.props.isGeolocationAvailable
      ? <div>Your browser does not support Geolocation</div>
      : !this.props.isGeolocationEnabled
        ? <div>Geolocation is not enabled</div>
        : this.props.coords
          ? <table>
            <tbody>
              <tr><td>latitude</td><td>{this.props.coords.latitude}</td></tr>
              <tr><td>longitude</td><td>{this.props.coords.longitude}</td></tr>
            </tbody>
          </table>
          : <div>Getting the location data&hellip; </div>;
  }

  render() {
    const { error, isLoaded, myCoords, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div>
        <p>
          {items.words}
        </p>
      <table>
      <tbody>
        <tr><td>latitude</td><td>{myCoords.lat}</td></tr>
              <tr><td>longitude</td><td>{myCoords.long}</td></tr>
            </tbody>
            </table>
        {this.getCoordsDom()}

        </div>
      );
    }
  }
}

export default geolocated({
  positionOptions: {
    enableHighAccuracy: false,
  },
  userDecisionTimeout: 5000,
}) (MyComponent);
